import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSocket } from '../contexts/SocketContext';
import Chat from './Chat';

const StudentResults = () => {
  const [results, setResults] = useState(null);
  const [question, setQuestion] = useState(null);
  const [showChat, setShowChat] = useState(false);
  const navigate = useNavigate();
  const { socket, unreadMessages } = useSocket();

  useEffect(() => {
    console.log('StudentResults: Component mounted, current state:', { question, results });
  }, [question, results]);

  useEffect(() => {
    if (socket) {
      socket.on('poll-results', (data) => {
        console.log('StudentResults: Received poll results data:', data);
        setQuestion(data.question);
        setResults(data.results);
        console.log('StudentResults: Set question and results:', { question: data.question, results: data.results });
      });

      socket.on('new-question', () => {
        navigate('/student/poll');
      });

      socket.on('student-kick', () => {
        navigate('/student/kicked');
      });

      return () => {
        socket.off('poll-results');
        socket.off('new-question');
        socket.off('student-kick');
      };
    }
  }, [socket, navigate]);

  if (!question || !results) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-violet border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted">Loading results...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-text">
            Question 1
          </h1>
          <div className="flex items-center space-x-2">
            <svg className="w-5 h-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
            </svg>
            <span className="text-lg font-semibold text-text">00:00</span>
          </div>
        </div>

        {/* Question */}
        <div className="bg-text text-white p-6 rounded-lg mb-6">
          <h2 className="text-xl font-semibold">{question.question}</h2>
        </div>

        {/* Results */}
        <div className="space-y-3 mb-6">
          {question.options.map((option, index) => {
            const result = results[index] || { count: 0, percentage: 0 };
            return (
              <div key={index} className="bg-white border border-gray-200 rounded-lg p-4">
                <div className="flex items-center space-x-4 mb-2">
                  <div className="w-8 h-8 rounded-full bg-violet text-white flex items-center justify-center text-sm font-semibold">
                    {index + 1}
                  </div>
                  <span className="flex-1 font-medium text-text">{option}</span>
                  <span className="text-muted font-semibold">{result.percentage}%</span>
                </div>
                
                {/* Progress Bar */}
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-violet h-2 rounded-full transition-all duration-500"
                    style={{ width: `${result.percentage}%` }}
                  ></div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Status Message */}
        <div className="text-center">
          <p className="text-muted">
            Wait for the teacher to ask a new question.
          </p>
        </div>
      </div>

      {/* Floating Chat Button */}
      <div className="fixed bottom-6 right-6">
        <button 
          onClick={() => setShowChat(true)}
          className="w-14 h-14 bg-violet rounded-full shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-110 flex items-center justify-center relative"
        >
          <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M18 10c0 3.866-3.582 7-8 7a8.841 8.841 0 01-4.083-.98L2 17l1.338-3.123C2.493 12.767 2 11.434 2 10c0-3.866 3.582-7 8-7s8 3.134 8 7zM7 9H5v2h2V9zm8 0h-2v2h2V9zM9 9h2v2H9V9z" clipRule="evenodd" />
          </svg>
          {unreadMessages > 0 && (
            <div className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
              {unreadMessages > 9 ? '9+' : unreadMessages}
            </div>
          )}
        </button>
      </div>

      {/* Chat Modal */}
      <Chat isOpen={showChat} onClose={() => setShowChat(false)} />
    </div>
  );
};

export default StudentResults;
