import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSocket } from '../contexts/SocketContext';

const StudentPollView = () => {
  const [question, setQuestion] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [timeLeft, setTimeLeft] = useState(0);
  const [hasSubmitted, setHasSubmitted] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { socket, submitAnswer } = useSocket();

  useEffect(() => {
    if (socket) {
      socket.on('new-question', (newQuestion) => {
        setQuestion(newQuestion);
        setTimeLeft(newQuestion.timer);
        setSelectedOption(null);
        setHasSubmitted(false);
        setError('');
      });

      socket.on('poll-results', () => {
        navigate('/student/results');
      });

      socket.on('student-kick', () => {
        navigate('/student/kicked');
      });

      socket.on('answer-submitted', () => {
        setHasSubmitted(true);
      });

      socket.on('error', (data) => {
        setError(data.message);
      });

      return () => {
        socket.off('new-question');
        socket.off('poll-results');
        socket.off('student-kick');
        socket.off('answer-submitted');
        socket.off('error');
      };
    }
  }, [socket, navigate]);

  useEffect(() => {
    if (timeLeft > 0 && !hasSubmitted) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [timeLeft, hasSubmitted]);

  const handleOptionSelect = (optionIndex) => {
    if (!hasSubmitted && timeLeft > 0) {
      setSelectedOption(optionIndex);
    }
  };

  const handleSubmit = () => {
    if (selectedOption !== null && !hasSubmitted && timeLeft > 0) {
      submitAnswer(question.id, selectedOption.toString());
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  if (!question) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-violet border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted">Loading question...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-text">
            Question 1
          </h1>
          <div className="flex items-center space-x-2">
            <svg className="w-5 h-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
            </svg>
            <span className={`text-lg font-semibold ${timeLeft <= 10 ? 'text-red-500' : 'text-text'}`}>
              {formatTime(timeLeft)}
            </span>
          </div>
        </div>

        {/* Question */}
        <div className="bg-text text-white p-6 rounded-lg mb-6">
          <h2 className="text-xl font-semibold">{question.question}</h2>
        </div>

        {/* Options */}
        <div className="space-y-3 mb-6">
          {question.options.map((option, index) => (
            <div
              key={index}
              onClick={() => handleOptionSelect(index)}
              className={`p-4 rounded-lg cursor-pointer transition-all duration-200 ${
                selectedOption === index
                  ? 'bg-violet text-white border-2 border-violet'
                  : 'bg-white border border-gray-200 hover:border-violet/50'
              } ${hasSubmitted ? 'cursor-not-allowed opacity-50' : ''}`}
            >
              <div className="flex items-center space-x-4">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold ${
                  selectedOption === index
                    ? 'bg-white text-violet'
                    : 'bg-gray-100 text-text'
                }`}>
                  {index + 1}
                </div>
                <span className="flex-1">{option}</span>
              </div>
            </div>
          ))}
        </div>

        {/* Error Message */}
        {error && (
          <div className="mb-4 p-3 bg-red-50 text-red-500 rounded-lg">
            {error}
          </div>
        )}

        {/* Submit Button */}
        <div className="flex justify-end">
          <button
            onClick={handleSubmit}
            disabled={selectedOption === null || hasSubmitted || timeLeft === 0}
            className="px-6 py-3 bg-gradient-to-r from-primary to-accent text-white font-semibold rounded-lg hover:shadow-lg transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
          >
            {hasSubmitted ? 'Submitted' : 'Submit'}
          </button>
        </div>

        {/* Status Message */}
        {hasSubmitted && (
          <div className="mt-4 text-center">
            <p className="text-muted">
              Wait for the teacher to ask a new question.
            </p>
          </div>
        )}

        {timeLeft === 0 && !hasSubmitted && (
          <div className="mt-4 text-center">
            <p className="text-red-500 font-semibold">
              Time's up! You can no longer submit an answer.
            </p>
          </div>
        )}
      </div>

      {/* Floating Chat Button */}
      <div className="fixed bottom-6 right-6">
        <button className="w-14 h-14 bg-violet rounded-full shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-110 flex items-center justify-center">
          <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M18 10c0 3.866-3.582 7-8 7a8.841 8.841 0 01-4.083-.98L2 17l1.338-3.123C2.493 12.767 2 11.434 2 10c0-3.866 3.582-7 8-7s8 3.134 8 7zM7 9H5v2h2V9zm8 0h-2v2h2V9zM9 9h2v2H9V9z" clipRule="evenodd" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default StudentPollView;
