import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSocket } from '../contexts/SocketContext';

const TeacherDashboard = () => {
  const [activeQuestion, setActiveQuestion] = useState(null);
  const [students, setStudents] = useState([]);
  const [responses, setResponses] = useState({});
  const [showParticipants, setShowParticipants] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [allStudentsAnswered, setAllStudentsAnswered] = useState(false);
  const [progress, setProgress] = useState({ answered: 0, total: 0 });
  const navigate = useNavigate();
  const { socket, kickStudent, endPoll } = useSocket();

  // Debug: Log socket status
  useEffect(() => {
    console.log("Teacher socket:", socket);
    console.log("Teacher socket connected:", socket?.connected);
  }, [socket]);

  // Join as teacher when socket is connected
  useEffect(() => {
    if (socket && socket.connected) {
      console.log('TeacherDashboard: Joining as teacher');
      socket.emit('join-teacher');
    }
  }, [socket, socket?.connected]);

  useEffect(() => {
    if (socket && socket.connected) {
      console.log('TeacherDashboard: Socket connected, setting up listeners');
      
      socket.on('joined', (data) => {
        console.log('TeacherDashboard: Joined successfully:', data);
        if (data.activeQuestion) {
          setActiveQuestion(data.activeQuestion);
        }
        if (data.students) {
          setStudents(data.students.filter(s => s.role === 'student'));
        }
      });

      socket.on('student-joined', (data) => {
        console.log('TeacherDashboard: Student joined:', data);
        setStudents(prev => [...prev, data.student]);
      });

      socket.on('student-disconnected', (data) => {
        console.log('TeacherDashboard: Student disconnected:', data);
        setStudents(prev => prev.filter(s => s.id !== data.studentId));
      });

      socket.on('student-answered', (data) => {
        console.log('TeacherDashboard: Student answered:', data);
        setResponses(prev => ({
          ...prev,
          [data.student]: data.selectedOption
        }));
        
        // Update progress
        const newAnsweredCount = Object.keys(responses).length + 1;
        const totalStudents = students.length;
        setProgress({ answered: newAnsweredCount, total: totalStudents });
      });

      socket.on('all-students-answered', (data) => {
        console.log('TeacherDashboard: All students answered:', data);
        setAllStudentsAnswered(true);
        setProgress({ answered: data.answeredStudents, total: data.totalStudents });
      });

      socket.on('poll-results', (data) => {
        console.log('TeacherDashboard: Poll results:', data);
        setActiveQuestion(null);
        setResponses({});
        setAllStudentsAnswered(false);
        setProgress({ answered: 0, total: 0 });
      });

      return () => {
        console.log('TeacherDashboard: Cleaning up socket listeners');
        socket.off('joined');
        socket.off('student-joined');
        socket.off('student-disconnected');
        socket.off('student-answered');
        socket.off('poll-results');
      };
    }
  }, [socket, socket?.connected]);

  const handleKickStudent = (studentId) => {
    kickStudent(studentId);
    setStudents(prev => prev.filter(s => s.id !== studentId));
  };

  const calculateResults = () => {
    if (!activeQuestion) return {};
    
    const results = {};
    const totalResponses = Object.keys(responses).length;
    
    activeQuestion.options.forEach((option, index) => {
      const count = Object.values(responses).filter(r => r === index.toString()).length;
      results[index] = {
        option,
        count,
        percentage: totalResponses > 0 ? Math.round((count / totalResponses) * 100) : 0
      };
    });
    
    return results;
  };

  const results = calculateResults();

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-text">Question</h1>
          <button
            onClick={() => navigate('/teacher/history')}
            className="flex items-center space-x-2 px-4 py-2 bg-violet text-white rounded-lg hover:bg-primary transition-colors"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
              <path fillRule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clipRule="evenodd" />
            </svg>
            <span>View Poll history</span>
          </button>
        </div>

        {activeQuestion ? (
          <>
            {/* Active Question */}
            <div className="bg-text text-white p-6 rounded-lg mb-6">
              <h2 className="text-xl font-semibold">{activeQuestion.question}</h2>
            </div>

            {/* Progress Bar */}
            <div className="mb-6">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm font-medium text-text">Student Progress</span>
                <span className="text-sm text-muted">{progress.answered}/{progress.total} answered</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-green-500 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${progress.total > 0 ? (progress.answered / progress.total) * 100 : 0}%` }}
                ></div>
              </div>
              {allStudentsAnswered && (
                <div className="mt-3 p-3 bg-green-50 border border-green-200 rounded-lg">
                  <div className="flex items-center space-x-2">
                    <svg className="w-5 h-5 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                    </svg>
                    <span className="text-green-700 font-medium">All students have answered! You can now create a new question.</span>
                  </div>
                </div>
              )}
            </div>

            {/* Live Results */}
            <div className="space-y-3 mb-6">
              {activeQuestion.options.map((option, index) => {
                const result = results[index] || { count: 0, percentage: 0 };
                return (
                  <div key={index} className="bg-white border border-gray-200 rounded-lg p-4">
                    <div className="flex items-center space-x-4 mb-2">
                      <div className="w-8 h-8 rounded-full bg-violet text-white flex items-center justify-center text-sm font-semibold">
                        {index + 1}
                      </div>
                      <span className="flex-1 font-medium text-text">{option}</span>
                      <span className="text-muted font-semibold">{result.percentage}%</span>
                    </div>
                    
                    {/* Progress Bar */}
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-violet h-2 rounded-full transition-all duration-500"
                        style={{ width: `${result.percentage}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Action Buttons */}
            <div className="flex justify-center">
              <button
                onClick={() => endPoll()}
                className="px-6 py-3 bg-gradient-to-r from-primary to-accent text-white font-semibold rounded-lg hover:shadow-lg transition-all duration-200 transform hover:scale-105"
              >
                End Poll
              </button>
            </div>
          </>
        ) : (
          /* No Active Question */
          <div className="text-center py-12">
            <h2 className="text-xl font-semibold text-text mb-4">No active poll</h2>
            <button
              onClick={() => navigate('/teacher/create-poll')}
              className="px-6 py-3 bg-gradient-to-r from-primary to-accent text-white font-semibold rounded-lg hover:shadow-lg transition-all duration-200 transform hover:scale-105"
            >
              + Ask a new question
            </button>
          </div>
        )}

        {/* Participants Modal */}
        {showParticipants && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg p-6 w-full max-w-md">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-text">Participants</h3>
                <button
                  onClick={() => setShowParticipants(false)}
                  className="text-muted hover:text-text"
                >
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </button>
              </div>
              
              <div className="space-y-2">
                {students.map((student) => (
                  <div key={student.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <span className="text-text">{student.name}</span>
                    <button
                      onClick={() => handleKickStudent(student.id)}
                      className="text-accent hover:text-primary text-sm font-medium"
                    >
                      Kick out
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Floating Chat Button */}
      <div className="fixed bottom-6 right-6">
        <button 
          onClick={() => setShowParticipants(true)}
          className="w-14 h-14 bg-violet rounded-full shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-110 flex items-center justify-center"
        >
          <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 20 20">
            <path d="M13 6a3 3 0 11-6 0 3 3 0 016 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3z" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default TeacherDashboard;
